import bcrypt
from hashlib import blake2b


def bcrypt_get_hashed_password(plain_text_password):
  plain_text_password = plain_text_password.encode()  # converts to bytes
  # the salt is generated by bcrypt and stored inside the hash
  return bcrypt.hashpw(plain_text_password, bcrypt.gensalt())


def hashlib_get_hashed_password(plaint_text_password):
  return blake2b(plaint_text_password.encode()).hexdigest()


hash = { # exports functions
    'bcrypt': bcrypt_get_hashed_password,
    'hashlib': hashlib_get_hashed_password
}
